// Componente donde quieres mostrar los productos de línea según la categoría seleccionada
import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getLineProducts } from "../Redux/Actions";

const Menu = () => {
  const dispatch = useDispatch();

  // Obtenemos la categoría seleccionada desde el estado de Redux
  const selectedCategory = useSelector((state) => state.selectedCategory);

  // Obtenemos los productos de línea desde el estado de Redux
  const lineProducts = useSelector((state) => state.lineProducts);

  // Llamamos a la función getLineProducts con la categoría seleccionada para obtener los productos filtrados
  useEffect(() => {
    dispatch(getLineProducts(selectedCategory));
  }, [dispatch, selectedCategory]);

  return (
    <div>
      <h2>Productos de línea para la categoría: {selectedCategory}</h2>
      <ul>
        {lineProducts.map((producto) => (
          <li key={producto.id}>
            <strong>Nombre:</strong> {producto.name} <br />
            <strong>Línea:</strong> {producto.lines}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Menu;
