import React from "react";
import "./Menu.css";
import { useDispatch, useSelector } from "react-redux";
import { Link } from 'react-router-dom';
import { getLineProducts } from '../Redux/Actions';

export default function Menu() {
  const dispatch = useDispatch();
  const categories = ['Bathroom', 'Kitchen', 'Home & Deco', 'Personal care', 'Disposables'];

  // Obtiene la categoría seleccionada del estado global de Redux
  const selectedCategory = useSelector((state) => state.selectedCategory);

  // Función para manejar el clic en una categoría
  function handleClick(category) {
    // Si la categoría seleccionada es la misma que se hizo clic, entonces no es necesario obtener nuevas cards
    if (selectedCategory === category) {
      return;
    }

    // Si la categoría seleccionada es diferente a la que se hizo clic, obtén las cards de la categoría seleccionada
    const encodedCategory = encodeURIComponent(category.toLowerCase().replace(/\s/g, '-'));
    dispatch(getLineProducts(encodedCategory));
  }

  return (
    <div className="menu">
      <link href="https://fonts.googleapis.com/css2?family=Raleway:wght@400;700&display=swap" rel="stylesheet"></link>
      <ul className="menu ul">
        <li key="all" className="menu li">
          <Link to="/" className={`menu-link ${!selectedCategory ? 'active' : ''}`} onClick={() => handleClick(null)}>
            All {/* Enlace para mostrar todas las cards */}
          </Link>
        </li>
        {categories.map((category, index) => (
          <li key={index} className="menu li">
            <Link
              to={`/products/${category.toLowerCase().replace(/\s/g, '-')}`}
              className={`menu-link ${selectedCategory === category ? 'active' : ''}`}
              onClick={() => handleClick(category)}
            >
              {category}
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
}
