import React from "react";
import { useAuth0 } from "@auth0/auth0-react";
import { createUser, getUserInfo } from "../../Redux/Actions";

// export default function SignupButton () {
//     const { loginWithRedirect, user} = useAuth0();

// //console.log(user)
//     return(
//             <button onClick={()=>loginWithRedirect()} className="btn btn-outline-dark btn-sm ms-4 ps-3 pe-3">Sign up </button>
//     )
// };

export default function SignupButton() {
    const { loginWithRedirect, user, isAuthenticated } = useAuth0();
  
    const createUserInDatabase = (user) => {
      // Aquí va la lógica para crear el usuario en la base de datos
      // Puedes implementar la función createUser o realizar una solicitud a tu API
      // para guardar los datos del usuario en tu base de datos
      const userDb = {
        email: user.email,
        name: user.name,
        // Otros datos del usuario que deseas guardar en la base de datos
      };
  
      // Ejemplo usando una función createUser que deberías definir en tu código
      createUser(userDb)
        .then(() => {
          console.log("Usuario creado exitosamente en la base de datos");
        })
        .catch((error) => {
          console.error("Error al crear el usuario en la base de datos:", error);
        });
    };
  
    // Lógica para crear el usuario en la base de datos cuando el usuario se registra
    if (isAuthenticated && user) {
      // Verifica si el usuario se ha registrado por primera vez para evitar llamadas duplicadas
      if (user.sub.includes("auth0|")) {
        createUserInDatabase(user);
      }
    }
  
    return (
      <button onClick={() => loginWithRedirect()} className="btn btn-outline-dark btn-sm ms-4 ps-3 pe-3">Sign up</button>
    );
  };
