import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getProducts, getLineProducts } from '../Redux/Actions';
import Card from './Card';
import Navbar from "./Navbar";
import Menu from "./Menu";
import Footer from "./Footer";
import "./Home.css";
import { Route, Switch } from "react-router-dom";
import { ScrollMenu, VisibilityContext } from 'react-horizontal-scrolling-menu';

export default function Home() {
  const dispatch = useDispatch();
  const allProducts = useSelector((state) => state.products);
  const selectedCategory = useSelector((state) => state.selectedCategory);

  useEffect(() => {
    dispatch(getProducts());
  }, [dispatch]);

  useEffect(() => {
    if (selectedCategory) {
      dispatch(getLineProducts(selectedCategory));
    }
  }, [dispatch, selectedCategory]);

  const renderCards = () => {
    return allProducts?.map((el) => (
      <div key={el.id} className="card-container">
        <Card
          images={el.images}
          name={el.name}
          price={el.price}
          id={el.id}
        />
      </div>
    ));
  };

  return (
    <div>
      <Navbar />
      <Menu />
      <link href="https://fonts.googleapis.com/css2?family=Raleway:wght@400;700&display=swap" rel="stylesheet" />
      <div>
        <ScrollMenu
          data={renderCards()}
          arrowLeft={<div className="arrow-prev">&#60;</div>}
          arrowRight={<div className="arrow-next">&#62;</div>}
          hideArrows={false}
          hideSingleArrow={true}
          scrollToSelected={true}
          alignCenter={false}
          dragging={true}
          clickWhenDrag={false}
          wheel={false}
          transition={0.4}
          selected={0}
          translate={0}
          alignOnResize={true}
          onUpdate={(visibleItems) => {
            // Hacer algo con los elementos visibles
          }}
        />
      </div>
      <Footer />
    </div>
  );
}
