export default function ProductDetail({ id }) {
  const { name, price, stock, details, line, images } = useSelector((state) => state.detail);
  const dispatch = useDispatch();

  useEffect(() => { 
    dispatch(getDetail(id));
  }, [dispatch, id]);

  return (
    <div class="contenedor-principal">
      {details && (
        <div class="contenedor">
          <div class="card-imagen">
            <img src={images} alt="Not found" />
          </div>
          <div className="detalle">
            <p class="detalle-texto">Name: {name}</p>
            <p class="detalle-texto">Price: {price}</p>
            <p class="detalle-texto">Stock: {stock}</p>
            <p class="detalle-texto">Detail: {details}</p>
            <p class="detalle-texto">Line: {line}</p>
          </div>
        </div>
      )}
    </div>
  );
}
