import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";


export default function CartCard({ id, images, name, email, details }) {

    const imageUrls = images.split(", ");
    const firstImageUrl = imageUrls[0]; // Obtener la primera URL de imagen

    return(
        <div className="cardd" style={{ width: "1300px", height: "150px", margin: "5px", boxShadow: "0 0 2px 0px rgba(0, 0, 0, 0.5)", borderRadius: "20px" }}>
            <Link to={`/${id}`} style={{ textDecoration: "none" }}>
            <img src={firstImageUrl} alt="no" className="card-img-top" style={{ height: "100px", width: "1300px", objectFit: "cover", borderTopRightRadius: "20px", borderTopLeftRadius: "20px" }} />

            <h5 className="" style={{ fontWeight: "normal", color: "black" }}>{name}</h5>
            </Link>
        </div>
    )
}
