import React, { useEffect } from "react";
import { useAuth0 } from "@auth0/auth0-react";
import { createUser, getUserInfo } from "../../Redux/Actions";
import { useDispatch, useSelector } from "react-redux";
import Navbar from "../Navbar";
import Menu from "../Menu";
import Footer from "../Footer";

const UserProfile = () => {
  const dispatch = useDispatch();
  const { user, isAuthenticated, isLoading } = useAuth0();
  const userInfo = useSelector((state) => state.userInfo);


  useEffect(() => {
    if (isAuthenticated && !isLoading) {
      const userDbData = {
        email: user.email,
        fullname: user.name,
        profile: user.nickname,
        avatar: user.picture,
      };

      dispatch(createUser(userDbData));
      dispatch(getUserInfo());
    }
  }, [dispatch, isAuthenticated, isLoading, user]);

  if (isLoading) {
    // You can add a loading spinner or placeholder while the authentication is in progress
    return <div>Loading...</div>;
  }

  // Check if the userDb and user objects have the same email (or any other unique identifier)
//   userInfo.map((e) => {
//     console.log(e.email);
//   });

// var hasUserDbData;
// for (let i = 0; i < userInfo.length; i++) {
//   if (userInfo[i].email === user.email) {
//     hasUserDbData = user.email;
//     // Realizar acciones adicionales si lo deseas
//     // ...
//     break; // Salir del bucle una vez que se encuentra la coincidencia
//   }
// }

//console.log(hasUserDbData)
  
return (
    isAuthenticated && (
      <div>
        <div>
          <Navbar />
        </div>
        <div>
          <Menu />
        </div>
        <div> {/* Agrega un div como elemento contenedor */}
          {userInfo.map((el) => {
            // Check if the email of userInfo matches the email of user
            if (el.email === user.email) {
              return (
                <div key={el.id}>
                  <img src={el.avatar} alt={el.fullname} />
                  <h2>{el.fullname}</h2>
                  <p>{el.email}</p>
                </div>
              );
            } else {
              // Si no hay coincidencia, puedes renderizar otro contenido o dejarlo en blanco
              return null;
            }
          })}
        </div>
        <div>
          <Footer />
        </div>
      </div>
    )
  );
        }  
export default UserProfile;


