import { GET_PRODUCTS, GET_PRODUCT_DETAIL, GET_LINE_PRODUCTS, SET_LINE, FOOTER, SEARCH_SUCCESS, SET_SEARCH, CREATE_USER} from "./Actions";


const initialState = {
    products: [],
    filtered: [],
    detail: [],
    selectedCategory: "",
    footer: false,
    isSearch: false,
    // searchResults: [], 
    // searchError: null,
    isLine: false,
    userInfo: {},
}

function reducer (state = initialState, action) {
switch (action.type) {
    case GET_PRODUCTS:
        return{
            ...state,
            products: action.payload,
            filtered: action.payload,
        };
    case GET_PRODUCT_DETAIL:
        return{
            ...state,
            detail: action.payload,
        }

        case GET_LINE_PRODUCTS:
          const { payload } = action;
          const filteredProducts = payload.filter((product) => {
            // Verifica si el producto tiene líneas asociadas y si alguna de las líneas tiene un nombre válido
            return product.lines.some((line) => line.name);
          });
        
          return {
            ...state,
            filtered: filteredProducts[0]?.lines[0]?.name || "", // Esto establecerá el nombre de la primera línea válida encontrada en los productos filtrados
            products: filteredProducts,
          };
      case SET_LINE:
        return {
        ...state,
        isLine: true,
      };

        case FOOTER:
      return {
        ...state,
        footer: !state.footer,
      };

      ////
      case SEARCH_SUCCESS:
        return {
            ...state,
            products: action.payload,
        };
      
      case SET_SEARCH:
      return {
        ...state,
        isSearch: true,
      };

      case CREATE_USER:
        if (action.payload.id) {
          return {
            ...state,
            userInfo: { status: "User Created" },
          };
        }
     

    default:
        return state
      }}
      export default reducer;
