import React from "react";
import SearchBar from "./SearchBar";
import "./Navbar.css";
import EcoEcho from "../Fotos/EcoEcho.png";
import { Link } from "react-router-dom";
import { useAuth0 } from "@auth0/auth0-react";
import Login from "./Login/Login";
import LogoutButton from "./Login/LogoutButton";
import SignupButton from "./Login/Signup";
import { setShowSlice } from "../Redux/Actions";
import { useDispatch, useSelector } from "react-redux";

export default function Navbar() {
  const dispatch = useDispatch();
  const { isAuthenticated } = useAuth0();
  const showSlice = useSelector((state) => state.showSlice);

  useEffect(() => {
    dispatch(getProducts());
  }, [dispatch]);

  function handleSearch(name) {
    dispatch(setShowSlice(false));
    // Perform search functionality here
  }

  return (
    <header>
      <link
        href="https://fonts.googleapis.com/css2?family=Raleway:wght@400;700&display=swap"
        rel="stylesheet"
      ></link>
      <div className="logo"></div>
      <Link to="/">
        <img
          className="logoeco"
          src={EcoEcho}
          alt="EcoEcho"
          width="90"
          height="90"
        ></img>
      </Link>
      <div className="SearchBar">
        <SearchBar />
      </div>
      <h2 className="text-nav">EcoEcho - ecological products </h2>
      <div>

      {isAuthenticated ?
              (
                <>
                  <Link to="/userprofile" >Profile, </Link>
                  <LogoutButton />
                </>
              ) :
              (
                <>
                  <Login />
                  <SignupButton />
                </>
              )
            }
     
</div>
    </header>
  );
}
