import React from "react";
import { useAuth0 } from "@auth0/auth0-react";
import { useHistory } from "react-router-dom";

export default function SignupButton() {
  const { loginWithRedirect } = useAuth0();
  const history = useHistory();

  const handleSignupClick = () => {
    // Redirige al usuario al proceso de registro
    loginWithRedirect();
  };

  // Si el usuario se registra con éxito, se llamará a esta función de devolución de llamada.
  const handleRedirect = () => {
    // Actualiza la ruta a la página de inicio después de que el usuario se haya registrado
    history.push("/home");
  };

  // Agrega el evento de devolución de llamada para que se ejecute después de que el usuario haya sido registrado
  // Puedes usar el evento "onRedirectCallback" de Auth0 para esto
  const { user, isAuthenticated, isLoading } = useAuth0();
  useEffect(() => {
    if (isAuthenticated && !isLoading && user) {
      handleRedirect();
    }
  }, [isAuthenticated, isLoading, user]);

  return (
    <button onClick={handleSignupClick} className="btn btn-outline-dark btn-sm ms-4 ps-3 pe-3">
      Sign up
    </button>
  );
};
