import React, { useEffect } from "react";
import { useAuth0 } from "@auth0/auth0-react";
import { createUser, getUserInfo } from "../../Redux/Actions";
import { useDispatch, useSelector } from "react-redux";
import Navbar from "../Navbar";
import Menu from "../Menu";
import Footer from "../Footer";

const UserProfile = () => {
  const dispatch = useDispatch();
  const { user, isAuthenticated, isLoading } = useAuth0();

  // Obtener la información del usuario desde el estado de Redux
  const userDb = useSelector((state) => state.user);

  // useEffect para obtener la información del usuario desde la base de datos
  useEffect(() => {
    if (isAuthenticated && !isLoading) {
      const userDbData = {
        email: user.email,
        fullname: user.name,
        profile: user.nickname,
        avatar: user.picture,
      };

      // Llama a la acción createUser para guardar los datos del usuario en la base de datos si aún no existen
      dispatch(createUser(userDbData));

      // Llama a la acción getUserInfo para obtener la información actualizada del usuario desde la base de datos
      dispatch(getUserInfo());
    }
  }, [dispatch, isAuthenticated, isLoading, user]);

  // Mostrar información de la base de datos (userDb) si está disponible
  return (
    isAuthenticated && (
      <div>
        <div>
          <Navbar />
        </div>
        <div>
          <Menu />
        </div>
        {userDb && (
          <>
            <img src={userDb.avatar} alt={userDb.fullname} />
            <h2>{userDb.fullname}</h2>
            <p>{userDb.email}</p>
          </>
        )}

        <pre>{JSON.stringify(user)}</pre>
        <div>
          <Footer />
        </div>
      </div>
    )
  );
};

export default UserProfile;


