import React, { useEffect, useRef, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getProducts, getLineProducts } from '../Redux/Actions';
import Card from './Card';
import Navbar from "./Navbar";
import Menu from "./Menu";
import Footer from "./Footer";
import "./Home.css";
import ProductDetail from "./Details";
import { Route, Switch } from "react-router-dom";

export default function Home() {
  const dispatch = useDispatch();
  const allProducts = useSelector((state) => state.products);
  const selectedCategory = useSelector((state) => state.selectedCategory);
  const cardsContainerRef = useRef(null);
  const [scrollPosition, setScrollPosition] = useState(0);

  useEffect(() => {
    dispatch(getProducts());
  }, [dispatch]);

  useEffect(() => {
    if (selectedCategory) {
      dispatch(getLineProducts(selectedCategory));
    }
  }, [dispatch, selectedCategory]);

  const handleScroll = () => {
    const scrollOffset = cardsContainerRef.current.scrollLeft;
    setScrollPosition(scrollOffset);
  };

  const scrollLeft = () => {
    cardsContainerRef.current.scrollLeft -= 300;
  };

  const scrollRight = () => {
    cardsContainerRef.current.scrollLeft += 300;
  };

  return (
    <div>
      <Navbar />
      <Menu />
      <link href="https://fonts.googleapis.com/css2?family=Raleway:wght@400;700&display=swap" rel="stylesheet" />
      <div className="scroll-arrows">
        {scrollPosition > 0 && (
          <button className="arrow-prev" onClick={scrollLeft}>&#60;</button>
        )}
      </div>
      <div className="cards-container" ref={cardsContainerRef} onScroll={handleScroll}>
        {allProducts?.map((el) => (
          <Card key={el.id} images={el.images} name={el.name} price={el.price} id={el.id} />
        ))}
      </div>
      <div className="scroll-arrows">
        {scrollPosition < cardsContainerRef.current.scrollWidth - cardsContainerRef.current.offsetWidth && (
          <button className="arrow-next" onClick={scrollRight}>&#62;</button>
        )}
      </div>
      <Footer />
    </div>
  );
}
