import React from "react";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getProducts, getLineProducts } from '../Redux/Actions';
import Card from './Card';
import Navbar from "./Navbar";
import Menu from "./Menu";
import Footer from "./Footer";
import "./Home.css";
import ProductDetail from "./Details";
import { Route, Switch } from "react-router-dom";
import { ScrollMenu } from 'react-horizontal-scrolling-menu'; // Importar ScrollMenu específicamente



export default function Home() {
  const dispatch = useDispatch();
  const allProducts = useSelector((state) => state.products);
  const selectedCategory = useSelector((state) => state.selectedCategory);

  useEffect(() => {
    dispatch(getProducts());
  }, [dispatch]);

  useEffect(() => {
    if (selectedCategory) {
      dispatch(getLineProducts(selectedCategory));
    }
  }, [dispatch, selectedCategory]);

  return (
    <div>
      <Navbar />
      <Menu />
      <link href="https://fonts.googleapis.com/css2?family=Raleway:wght@400;700&display=swap" rel="stylesheet" />
      <div>
        <ScrollMenu
          data={allProducts?.map((el) => (
            <Card
              key={el.id}
              images={el.images}
              name={el.name}
              price={el.price}
              id={el.id}
            />
          ))}
     
        />
      </div>
      <Footer />
    </div>
  );
}
