import React, { useState, useEffect } from "react";
import "./AddedToCartModal.css";

export default function AddedToCartModal({ isOpen, onClose, addedProduct }) {
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    if (isOpen && addedProduct) {
      setShowModal(true);
      setTimeout(() => {
        setShowModal(false);
        onClose(); // Después de mostrar el mensaje, cerrarlo
      }, 3000); // Esperar 3 segundos para cerrarlo
    }
  }, [isOpen]);


  return (
    <div className={`added-to-cart-modal ${isOpen ? "show" : ""}`}>
      <p>Your item was added to the cart!</p>
      {addedProduct && (
        <ul>
        <li className="product-item-container">
          <img
            className="img-cart-detail"
            src={addedProduct.img}
            width="55"
            height="55"
            alt="Product"
          />
          <div>
            <p>Product: {addedProduct.name}</p>
            <p>Price: ${addedProduct.price}</p>
          </div>
        </li>
      </ul>
      )}
    </div>
  );
}
