// export default UserProfile;

const UserProfile = () => {
    const dispatch = useDispatch();
    const { user, isAuthenticated, isLoading } = useAuth0();
    console.log(user);
  
    const userDb = useSelector((state) => state.user); // Suponiendo que el estado de Redux contiene la información del usuario de la base de datos
  
    useEffect(() => {
      if (isAuthenticated && !isLoading) {
        const userDbData = {
          email: user.email,
          fullname: user.name,
          profile: user.nickname,
          avatar: user.picture,
        };
  
        dispatch(createUser(userDbData));
      }
    }, [dispatch, isAuthenticated, isLoading, user]);
  
    if (isLoading) {
      // You can add a loading spinner or placeholder while the authentication is in progress
      return <div>Loading...</div>;
    }
  
    // Check if the userDb and user objects have the same email (or any other unique identifier)
    const hasUserDbData = userDb.email === user.email;
  
    return (
      isAuthenticated && (
        <div>
          <div>
            <Navbar />
          </div>
          <div>
            <Menu />
          </div>
          {hasUserDbData ? (
            // Show data from the database (userDb) if available
            <>
              <img src={userDb.avatar} alt={userDb.fullname} />
              <h2>{userDb.fullname}</h2>
              <p>{userDb.email}</p>
            </>
          ) : (
            // Show data directly from Auth0 (user) if not available in the database
            <>
              <img src={user.picture} alt={user.name} />
              <h2>{user.name}</h2>
              <p>{user.email}</p>
              <h2>{user.nickname}</h2>
            </>
          )}
  
          <pre>{JSON.stringify(user)}</pre>
          <div>
            <Footer />
          </div>
        </div>
      )
    );
  };
  
  export default UserProfile;
