import React, { useEffect, useRef, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getProducts, getLineProducts } from '../Redux/Actions';
import Card from './Card';
import Navbar from "./Navbar";
import Menu from "./Menu";
import Footer from "./Footer";
import "./Home.css";

export default function Home() {
  const dispatch = useDispatch();
  const allProducts = useSelector((state) => state.products);
  const selectedCategory = useSelector((state) => state.selectedCategory);
  const cardsContainerRef = useRef(null);
  const [visibleProducts, setVisibleProducts] = useState([]);

  useEffect(() => {
    dispatch(getProducts());
  }, [dispatch]);

  useEffect(() => {
    if (selectedCategory) {
      dispatch(getLineProducts(selectedCategory));
    }
  }, [dispatch, selectedCategory]);

  useEffect(() => {
    setVisibleProducts(allProducts?.slice(0, 4));
  }, [allProducts]);

  const scrollLeft = () => {
    cardsContainerRef.current.scrollBy({
      left: -300,
      behavior: "smooth",
    });
  };

  const scrollRight = () => {
    cardsContainerRef.current.scrollBy({
      left: 300,
      behavior: "smooth",
    });
    const startIndex = visibleProducts.length;
    const endIndex = startIndex + 4;
    setVisibleProducts((prevVisibleProducts) => [
      ...prevVisibleProducts,
      ...allProducts.slice(startIndex, endIndex),
    ]);
  };

  return (
    <div>
      <Navbar />
      <Menu />
      <link href="https://fonts.googleapis.com/css2?family=Raleway:wght@400;700&display=swap" rel="stylesheet" />
      <div className="scroll-arrows">
        <button className="arrow-prev" onClick={scrollLeft}>&#60;</button>
      </div>
      <div className="cards-container" ref={cardsContainerRef}>
        {visibleProducts.map((el) => (
          <Card key={el.id} images={el.images} name={el.name} price={el.price} id={el.id} />
        ))}
      </div>
      <div className="scroll-arrows">
        <button className="arrow-next" onClick={scrollRight}>&#62;</button>
      </div>
      {allProducts?.length > visibleProducts.length && (
        <div className="scroll-arrows">
          <button className="arrow-prev" onClick={scrollLeft}>&#60;</button>
        </div>
      )}
      <Footer />
    </div>
  );
}
