import './App.css';
import { BrowserRouter, Route, Switch, useLocation } from 'react-router-dom';
import Home from './components/Home';
import ProductDetail from './components/Details';
import Information from './components/Information';
import UserProfile from './components/UserProfile/UserProfile';
import 'bootstrap/dist/css/bootstrap.min.css';
import Menu from './components/Menu';

function App() {
  const location = useLocation();
  const showSlider = !location.pathname.includes("/search");

  return (
    <BrowserRouter>
      <div className="App">
        <Menu />
        {showSlider && <Home />}
        <Switch>
          <Route exact path="/" component={Home} />
          <Route exact path="/information" component={Information} />
          <Route exact path="/userprofile" component={UserProfile} />
          <Route exact path="/:id" render={({ match }) => <ProductDetail id={match.params.id} />} />
          <Route exact path="/products/:lineParam" component={Home} />
        </Switch>
      </div>
    </BrowserRouter>
  );
}

export default App;
