import React, { useState, useEffect } from "react";
import "./AddedToCartModal.css";

export default function AddedToCartModal({ isOpen, onClose, addedProduct }) {
  const [showModal, setShowModal] = useState(false);
  const [productQueue, setProductQueue] = useState([]);


  useEffect(() => {
    if (isOpen && addedProducts.length > 0) {
      setShowModal(true);
      setProductQueue([...addedProducts]);
      setTimeout(() => {
        setShowModal(false);
        setProductQueue((prevQueue) => prevQueue.slice(1)); //sacar el primer item
        onClose(); // Después de mostrar el mensaje, cerrarlo
      }, 3000); // Esperar 3 segundos para cerrarlo
    }
  }, [isOpen]);


  return (
    <div className={`added-to-cart-modal ${isOpen ? "show" : ""}`}>
 {productQueue.length > 0 && (
        <div className="product-details">
          <img src={productQueue[0].img} width="100" height="100" />
          <div className="product-info">
            <p>
              <b>Product:</b> {productQueue[0].name}
            </p>
            <p>
              <b>Price:</b> ${productQueue[0].price}
            </p>
          </div>
        </div>
      )}
      <div className="message">
        <p><b>Your item was added to the cart!</b></p>
      </div>
    </div>
  );
}
