import React from "react";
import StarRatings from 'react-star-ratings';
//import "./Rating.css"

export default function Rating({ id, rating, review, productId, userId, user, promedioRating }) {
  // Verifica si promedioRating es un número antes de llamar a toFixed
  const formattedRating = typeof promedioRating === "number" ? promedioRating.toFixed(2) : "N/A";

  // Calcula el porcentaje de estrellas basado en el promedioRating
  const starPercentage = (formattedRating / 5) * 100;

  return (
    <div className="rating-card">
      <h5>Average rating</h5>
      <p>{formattedRating}</p>
      <div className="rating">
        <StarRatings
          rating={promedioRating} // Valor decimal del rating (por ejemplo, 4.5)
          starRatedColor="green"
          starDimension="35px"
          numberOfStars={5} // Número total de estrellas
          starSpacing="1px" // Espaciado entre estrellas
          starHoverColor="green" // Color al pasar el mouse sobre las estrellas
        />
      </div>
      {/* Puedes mostrar más detalles de la revisión aquí */}
    </div>
  );
}
