import React from "react";
import { Link } from "react-router-dom";
import "./Card.css";

export default function Card({ id, images, name, price, isFirstCard }) {
  const imageUrls = images.split(", ");
  const firstImageUrl = imageUrls[0]; // Obtener la primera URL de imagen

  return (
    <Link to={`/${id}`} className={`card-link ${isFirstCard ? "first-card" : ""}`}>
      <div className="card-container">
        <div className="image-container">
          {/* Mostrar solo la primera URL de imagen */}
          <img src={firstImageUrl} alt="no" />
        </div>
        <div className="card-content">
          <h2 className="card-name">{name}</h2>
          <h2 className="card-price">${price}</h2>
        </div>
      </div>
    </Link>
  );
}
