import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getProducts, getLineProducts } from '../Redux/Actions';
import Paginado from "./Cards/Paginado";
import Navbar from "./Navbar";
import Menu from "./Menu";
import Footer from "./Footer";
import Slider from "./Slider";
import "./Slider.css";
import "./Home.css";

import ProductDetail from "./Details";
import { Route, Switch, useLocation } from "react-router-dom";

export default function Home() {
  const dispatch = useDispatch();
  const allProducts = useSelector((state) => state.products);
  const selectedCategory = useSelector((state) => state.selectedCategory);
  const [showSlider, setShowSlider] = useState(true);
  const location = useLocation();

  useEffect(() => {
    dispatch(getProducts());
  }, [dispatch]);

  useEffect(() => {
    if (selectedCategory) {
      dispatch(getLineProducts(selectedCategory));
    }
  }, [dispatch, selectedCategory]);

  useEffect(() => {
    setShowSlider(!location.pathname.includes("/search"));
  }, [location.pathname]);

  return (
    <div>
      <Navbar />
      <Menu />
      {showSlider && <Slider />}
      <link
        href="https://fonts.googleapis.com/css2?family=Raleway:wght@400;700&display=swap"
        rel="stylesheet"
      ></link>
      <Switch>
        <Route exact path="/">
          <div>
            <Paginado cards={allProducts} />
          </div>
        </Route>
        <Route exact path="/search">
          <div>
            <Paginado cards={allProducts} />
          </div>
        </Route>
        <Route exact path="/product/:id" component={ProductDetail} />
      </Switch>
      <Footer />
    </div>
  );
}
