import React, { useEffect, useState } from "react";
import { useAuth0 } from "@auth0/auth0-react";
import { Link } from "react-router-dom";
import { getUserInfo } from "../../Redux/Actions";
import { useDispatch, useSelector } from "react-redux";
import Navbar from "../Navbar";
import Menu from "../Menu";
import Footer from "../Footer";
import "./UserProfile.css";

const UserProfile = ({ id }) => {
  const dispatch = useDispatch();
  const { user } = useAuth0();
  const userInfo = useSelector((state) => state.userInfo);
  const [userObj, setUserObj] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    setLoading(true);
    setError(null);

    dispatch(getUserInfo(id))
      .then((data) => {
        const foundUser = data.find((userObj) => userObj.email === user.email);
        if (foundUser) {
          setUserObj(foundUser);
        } else {
          setError("User not found.");
        }
      })
      .catch((err) => {
        setError("Error fetching user info.");
      })
      .finally(() => {
        setLoading(false);
      });
  }, [dispatch, id, user.email]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div>
      <div>
        <Navbar />
      </div>
      <div>
        <Menu />
      </div>
      <div className="mi-cuenta">Mi cuenta</div>

      <div className="container">
        <div className="container mt-4">
          <h5 className="card-title card-title-divider">Datos</h5>

          <div className="card">
            <div className="card-body">
              <h5 className="card-title">Datos Personales</h5>
              {userObj && (
                <div>
                  <img src={userObj.avatar} alt={userObj.name} />
                  <p>Nombre: {userObj.name}</p>
                  <p>Email: {userObj.email}</p>
                  <p>Profile: {userObj.profile}</p>
                </div>
              )}
            </div>
          </div>
        </div>

        <div className="container">
          <div className="container mt-4">
            <h5 className="card-title card-title-dividerr">Mis compras</h5>
            <div>¡Hace tu primera compra!</div>
            <Link to="/" className="black-button">
              IR A LA TIENDA
            </Link>
          </div>
        </div>
      </div>

      <div>
        <Footer />
      </div>
    </div>
  );
};

export default UserProfile;


