import React, { useState } from "react";
import Card from "./Card";
import "./Paginado.css";
import { Row, Col } from "react-bootstrap"; // Importa las clases de Bootstrap
import 'bootstrap/dist/css/bootstrap.css';

const Paginado = ({ cards }) => {
  // ...
  return (
    <div className="paginado-container">
      <div>
        <button onClick={handlePrevClick} disabled={startIndex === 0}>
          &lt; Anterior
        </button>
      </div>
      <Row className="card-container">
        {visibleCards.map((card, index) => (
          <Col sm={6} md={4} lg={3} key={index}>
            <Card
              id={card.id}
              images={card.images}
              name={card.name}
              price={card.price}
            />
          </Col>
        ))}
      </Row>
      <div>
        <button
          onClick={handleNextClick}
          disabled={startIndex + cardsPerPage >= cards.length}
        >
          Siguiente &gt;
        </button>
      </div>
    </div>
  );
};

export default Paginado;
