import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { getNameProduct } from "../Redux/Actions";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch } from '@fortawesome/free-solid-svg-icons';
import './SearchBar.css'; // Importa el archivo CSS para los estilos personalizados

export default function SearchBar() {
  const dispatch = useDispatch();
  const [name, setName] = useState("");

  function handleInputChange(e) {
    e.preventDefault();
    setName(e.target.value);
    console.log(name);
  }

  function handleSubmit(e) {
    e.preventDefault(e);
    dispatch(getNameProduct(name));
  }

  return (
    <div className="search-bar">
      <input
        id="search-input"
        type="text"
        placeholder="Search product"
        onChange={(e) => handleInputChange(e)}
        className="search-input"
      />
      <button type="submit" onClick={(e) => handleSubmit(e)} className="search-button">
        <FontAwesomeIcon icon={faSearch} className="search-icon" />
      </button>
    </div>
  );
}
