import React from "react";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getProducts, getLineProducts } from '../Redux/Actions';
import Card from './Card';
import Navbar from "./Navbar";
import Menu from "./Menu";
import Footer from "./Footer";
import "./Home.css";
import { ScrollMenu, VisibilityContext } from 'react-horizontal-scrolling-menu';

export default function Home() {
  const dispatch = useDispatch();
  const allProducts = useSelector((state) => state.products);
  const selectedCategory = useSelector((state) => state.selectedCategory);

  useEffect(() => {
    dispatch(getProducts());
  }, [dispatch]);

  useEffect(() => {
    if (selectedCategory) {
      dispatch(getLineProducts(selectedCategory));
    }
  }, [dispatch, selectedCategory]);

  return (
    <div>
      <Navbar />
      <Menu />
      <link
        href="https://fonts.googleapis.com/css2?family=Raleway:wght@400;700&display=swap"
        rel="stylesheet"
      />
      <div className="home-container">
        <ScrollMenu
          data={allProducts?.map((el) => (
            <Card
              key={el.id}
              images={el.images}
              name={el.name}
              price={el.price}
              id={el.id}
            />
          ))}
          arrowLeft={<div className="arrow-prev">&#60;</div>}
          arrowRight={<div className="arrow-next">&#62;</div>}
          hideSingleArrow={true}
          wheel={false}
          alignCenter={false}
          dragging={true}
          transition={0.4}
          innerWrapperStyle={{
            display: "flex",
            alignItems: "center",
          }}
        />
      </div>
      <Footer />
    </div>
  );
}
