import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getProducts, getLineProducts } from '../Redux/Actions';
import Card from './Card';
import Navbar from "./Navbar";
import Menu from "./Menu";
import Footer from "./Footer";
import "./Home.css";
import { ScrollMenu, VisibilityContext } from 'react-horizontal-scrolling-menu';

export default function Home() {
  const dispatch = useDispatch();
  const allProducts = useSelector((state) => state.products);
  const selectedCategory = useSelector((state) => state.selectedCategory);

  useEffect(() => {
    dispatch(getProducts());
  }, [dispatch]);

  useEffect(() => {
    if (selectedCategory) {
      dispatch(getLineProducts(selectedCategory));
    }
  }, [dispatch, selectedCategory]);

  const MenuItem = ({ children }) => {
    const visibility = React.useContext(VisibilityContext);
    return <div className={`menu-item ${visibility.isFirst || visibility.isLast ? "active" : ""}`}>{children}</div>;
  };

  return (
    <div>
      <Navbar />
      <Menu />
      <link href="https://fonts.googleapis.com/css2?family=Raleway:wght@400;700&display=swap" rel="stylesheet" />
      <div className="cards-container">
        <ScrollMenu
          data={allProducts?.map((el) => (
            <MenuItem key={el.id}>
              <Card images={el.images} name={el.name} price={el.price} id={el.id} />
            </MenuItem>
          ))}
          arrowLeft={<div className="arrow-prev">&#60;</div>}
          arrowRight={<div className="arrow-next">&#62;</div>}
          hideArrows={true}
          hideSingleArrow={true}
          alignCenter={false}
          scrollToSelected={true}
          dragging={true}
          wheel={true}
          transition={0.4}
          alignOnResize={true}
        />
      </div>
      <Footer />
    </div>
  );
}
