import React, { useEffect, useState } from "react";
import { useAuth0 } from "@auth0/auth0-react";
import { useDispatch, useSelector } from 'react-redux';
import { getUserInfo, getBuys } from "../../Redux/Actions";
import CartCard from "./CartCard";

export default function UserInfo({ id }){
    const dispatch = useDispatch();
    const { user } = useAuth0();
    const userInfo = useSelector((state) => state.userInfo);
    const buys = useSelector((state) => state.buys);
    console.log(buys)

    useEffect(() => {
        dispatch(getUserInfo(id));
        dispatch(getBuys())
      }, [dispatch, id]);
    

      const userBuys = buys.filter((buy) => buy.user.email === user.email);
  

    return(
        <div>
        {userBuys.map((buy) => (
            <CartCard 
            key={buy.id}
            id={buy.id}
            
            />
        //   <div key={buy.id}>
        //     {/* <h3>{buy.product.name}</h3>
        //     <p>{buy.product.details}</p> */}
        //     {/* Puedes mostrar otras propiedades del producto aquí */}
        //   </div>
        ))}
      </div>
   
    )
}
