import React, { useState } from "react";
import Card from "./Card";
import "./Paginado.css";

const Paginado = ({ cards }) => {
  const [visibleCardIndex, setVisibleCardIndex] = useState(0);
  const cardsPerPage = 4;

  const handlePrevClick = () => {
    setVisibleCardIndex((prevIndex) => Math.max(prevIndex - 1, 0));
  };

  const handleNextClick = () => {
    setVisibleCardIndex((prevIndex) =>
      Math.min(prevIndex + 1, cards.length - 1)
    );
  };

  const visibleCards = [cards[visibleCardIndex]];

  return (
    <div className="paginado-container">
      <div className="cards-container">
        <button
          className="pagination-button"
          onClick={handlePrevClick}
          disabled={visibleCardIndex === 0}
        >
          &lt;
        </button>
        {visibleCards.map((card, index) => (
          <Card
            key={index}
            id={card.id}
            images={card.images}
            name={card.name}
            price={card.price}
          />
        ))}
        <button
          className="pagination-button"
          onClick={handleNextClick}
          disabled={visibleCardIndex === cards.length - 1}
        >
          &gt;
        </button>
      </div>
    </div>
  );
};

export default Paginado;
