import React, { useState } from "react";
import Card from "./Card";
import "./Paginado.css";

const Paginado = ({ cards }) => {
  const [startIndex, setStartIndex] = useState(0);
  const cardsPerPage = 4;

  const handlePrevClick = () => {
    setStartIndex((prevIndex) => Math.max(prevIndex - cardsPerPage, 0));
  };

  const handleNextClick = () => {
    setStartIndex((prevIndex) =>
      Math.min(prevIndex + cardsPerPage, cards.length - cardsPerPage)
    );
  };

  const visibleCards = cards.slice(startIndex, startIndex + cardsPerPage);

  return (
    <div className="paginado-container">
      <div id="carouselExampleControls" className="carousel slide" data-bs-ride="carousel">
        <div className="carousel-inner">
          {visibleCards.map((card, index) => (
            <div key={index} className={`carousel-item ${index === 0 ? 'active' : ''}`}>
              <Card
                id={card.id}
                images={card.images}
                name={card.name}
                price={card.price}
              />
            </div>
          ))}
        </div>
        <button
          className="carousel-control-prev"
          type="button"
          data-bs-target="#carouselExampleControls"
          data-bs-slide="prev"
          onClick={handlePrevClick}
          disabled={startIndex === 0}
        >
          <span className="carousel-control-prev-icon" aria-hidden="true"></span>
          <span className="visually-hidden">Previous</span>
        </button>
        <button
          className="carousel-control-next"
          type="button"
          data-bs-target="#carouselExampleControls"
          data-bs-slide="next"
          onClick={handleNextClick}
          disabled={startIndex + cardsPerPage >= cards.length}
        >
          <span className="carousel-control-next-icon" aria-hidden="true"></span>
          <span className="visually-hidden">Next</span>
        </button>
      </div>
    </div>
  );
};

export default Paginado;
