import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getProducts, getLineProducts } from '../Redux/Actions';
import Card from "./Cards/Card";
import Navbar from "./Navbar";
import Menu from "./Menu";
import Footer from "./Footer";
import "./Home.css";
import ProductDetail from "./Details";
import { useParams } from "react-router-dom";

export default function LineProducts() {
  const dispatch = useDispatch();
  const allProducts = useSelector((state) => state.products);
  //const selectedCategory = useSelector((state) => state.selectedCategory);
  const { categoryName } = useParams();

  // Filtrar los productos según la categoría seleccionada o mostrar todos si no hay categoría seleccionada
  const filteredProducts = categoryName
    ? useSelector((state) => state.lineProducts)
    : allProducts;

  useEffect(() => {
    // Obtener las cards según la categoría seleccionada o mostrar todas si no hay categoría seleccionada
    if (categoryName) {
      const encodedCategory = encodeURIComponent(categoryName.toLowerCase().replace(/\s/g, '-'));
      dispatch(getLineProducts(encodedCategory));
    } else {
      dispatch(getProducts());
    }
  }, [dispatch, categoryName]);

  return (
    <div>
      <div>
        <Navbar />
      </div>
      <div>
        <Menu />
      </div>
      <link href="https://fonts.googleapis.com/css2?family=Raleway:wght@400;700&display=swap" rel="stylesheet"></link>
      <div>
        <div className="cards-container">
          {filteredProducts?.map((el) => {
            return (
              <div key={el.id}>
                <Card
                  images={el.images}
                  name={el.name}
                  id={el.id}
                />
              </div>
            );
          })}
        </div>
      </div>
      <div>
        <Footer />
      </div>
    </div>
  );
}
