import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import { useAuth0 } from "@auth0/auth0-react";
import { useDispatch, useSelector } from "react-redux";
import { setSearch } from "../Redux/Actions";
import SearchBar from "./SearchBar/SearchBar";
import EcoEcho from "../Fotos/EcoEcho.png";
import Login from "./Login/Login";
import LogoutButton from "./Login/LogoutButton";
import SignupButton from "./Login/Signup";
import "./Navbar.css";

export default function Navbar() {
  const dispatch = useDispatch();
  const isSearch = useSelector((state) => state.isSearch);
  const { isAuthenticated } = useAuth0();

  useEffect(() => {
    dispatch(setSearch(false)); // Establece el estado `isSearch` en `false` cuando el componente se monta
  }, []);

  const handleLogoClick = () => {
    if (isSearch) {
      return; // No hacer nada si isSearch ya es true
    }
    dispatch(setSearch(false)); // Envía la acción `setSearch` para establecer `isSearch` en `false`
  };

  return (
    <header>
      <link
        href="https://fonts.googleapis.com/css2?family=Raleway:wght@400;700&display=swap"
        rel="stylesheet"
      ></link>
      <div className="logo" onClick={handleLogoClick}>
        <Link to="/">
          <img
            className="logoeco"
            src={EcoEcho}
            alt="EcoEcho"
            width="90"
            height="90"
          ></img>
        </Link>
      </div>
      <div className="SearchBar">
        <SearchBar />
      </div>
      <h2 className="text-nav">EcoEcho - ecological products </h2>
      <div>
        {isAuthenticated ? (
          <>
            <Link to="/userprofile">Profile, </Link>
            <LogoutButton />
          </>
        ) : (
          <>
            <Login />
            <SignupButton />
          </>
        )}
      </div>
    </header>
  );
}
