import React, { useState, useEffect } from "react";
import "./AddedToCartModal.css";

export default function AddedToCartModal({ isOpen, onClose, addedItem }) {
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    if (isOpen && addedItem) {
      setShowModal(true);
      setTimeout(() => {
        setShowModal(false);
        onClose(); // Después de mostrar el mensaje, cerrarlo
      }, 3000); // Esperar 3 segundos para cerrarlo
    }
  }, [isOpen]);

  const productName = addedItem ? addedItem.name : "";
  const productPrice = addedItem ? addedItem.price : 0;

  return (
    <div className={`added-to-cart-modal ${showModal ? "show" : ""}`}>
      <p>Your item was added to the cart!</p>
      <p>Product: {productName}</p>
      <p>Price: ${productPrice}</p>
    </div>
  );
}
