import React, { useEffect, useState } from "react";
import { useAuth0 } from "@auth0/auth0-react";
import { createUser } from "../../Redux/Actions";
import { useDispatch, useSelector } from "react-redux";
import Navbar from "../Navbar";
import Menu from "../Menu";
import Footer from "../Footer";

const UserProfile = () => {
    const dispatch = useDispatch();
    //const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [loading, setLoading] = useState(true)
    const { user, isAuthenticated, isLoading } = useAuth0();
  
    //const { user, isAuthenticated, isLoading } = useAuth0();
    console.log(isAuthenticated)

    let userDb = {};

    if (user) {
        // console.log(user.AssigRoles[0])
        userDb = {
            email: user.email,
            name: user.name,
            picture: user.picture,
        }
    };

    useEffect(() => {
        if (userDb.email) {
            dispatch(createUser(userDb));
        }
    }, [dispatch, userDb.email]);

    useEffect(() => {
        if(userInfo.status === 'User Created'){
            dispatch(getAllUsers());
            dispatch(getUserInfo(userDb.email));
            dispatch(getUserWishlist(userDb.email));
            setLoading(isLoading);
            setIsAuthenticated(auth);
        }
    }, [dispatch, userInfo, isLoading, auth]);

 

    //hacer algo para que si el usuario no esta creado se cree y se renderice el componente infoUser donde tiene la info de cada usuario

    return (
        isAuthenticated &&
        <div>
            <div>
                <Navbar />
            </div>
            <div>
                <Menu />
            </div>
            <img src={user.picture} alt={user.name} />
            <h2>{user.name}</h2>
            <p>{user.email}</p>

            <pre>{JSON.stringify(user)}</pre>
            <div>
            <Footer />
        </div>
        </div>
    )

}

export default UserProfile;
