import React, { useEffect, useRef } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getProducts, getLineProducts, totalCart } from '../Redux/Actions';
import Paginado from "./Cards/Paginado";
import Navbar from "./Navbar";
import Menu from "./Menu";
import Footer from "./Footer";
import Slider from "./Slider";
import "./Slider.css";
import "./Home.css";
import { useParams } from "react-router-dom";
import ProductDetail from "./Details";
import { Route, Switch } from "react-router-dom";



export default function Home() {
  const dispatch = useDispatch();
  const { categoryName } = useParams();
  const allProducts = useSelector((state) => state.products);
  const selectedCategory = useSelector((state) => state.selectedCategory);
  const isSearch = useSelector((state) => state.isSearch); ////
  const isLine = useSelector((state) => state.isLine);

  const filteredProducts = allProducts.filter(
    (product) => product.category === categoryName
  );



  useEffect(() => {
    dispatch(getProducts());
  }, [dispatch]);

  useEffect(() => {
    if (selectedCategory) {
      dispatch(getLineProducts(selectedCategory));
    }
  }, [dispatch, selectedCategory]);

  useEffect(() => {
    // Fetch the total cart value when the Home component mounts
    totalCartRequestRef.current = dispatch(totalCart());

    return () => {
      // Cleanup: Cancel the totalCart request if it's still ongoing when the Home component unmounts
      if (totalCartRequestRef.current) {
        totalCartRequestRef.current.cancel();
      }
    };
  }, [dispatch]);


  return (
    <div >
    <div>
        <Navbar />
    </div>
    <div>
        <Menu />
    </div>
    <div>
    {!(isSearch || isLine) && <Slider />}


    </div>
    <link href="https://fonts.googleapis.com/css2?family=Raleway:wght@400;700&display=swap" rel="stylesheet"></link>
   
       <div>
      <div >
          <Paginado cards={filteredProducts} showAll={true} />
        </div>
        </div>
        <div>
      <Footer />
      </div>
    </div>
  );
}
