import React from "react";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux"
import { getProducts, getLineProducts } from '../Redux/Actions';
import Card from './Card';
import Navbar from "./Navbar";
import Menu from "./Menu";
import Footer from "./Footer";
import Slider from "./Slider";
import "./Home.css";
import image1 from '../Fotos/Mashstore-1.jpg';
import image2 from '../Fotos/Hilo2.jpg';
import image3 from '../Fotos/Essabo-3.jpg';
import image4 from '../Fotos/Ruh-4.jpg';


export default function Home() {
    const dispatch = useDispatch()
    const allProducts = useSelector((state) => state.products)
    const selectedCategory = useSelector((state) => state.selectedCategory);

  // Estados para el slider
  const [slidePictures, setSlidePictures] = useState([
    { url: image1 },
    { url: image2 },
    { url: image3 },
    { url: image4 }
    // Add more images as needed
  ]);
  const [currentIndex, setCurrentIndex] = useState(0);

    useEffect(() => {
        dispatch(getProducts());
    }, [dispatch])

    useEffect(() => {
        if (selectedCategory) {
          dispatch(getLineProducts(selectedCategory));
        }
      }, [dispatch, selectedCategory]);
    
    /* -------------------- PARA EL SLIDER ------------------------
    //useEffect para setear el intervalo en el que cambian
    useEffect(() => {
      const interval = setInterval(() => {
        setCurrentIndex((prevIndex) => (prevIndex + 1) % slidePictures.length);
      }, 3000);
  
      return () => {
        clearInterval(interval);
      };
    }, [slidePictures.length]);
  
    //constantes para manejar las flechas de previous y next
    const handlePrev = () => {
      setCurrentIndex((prevIndex) => (prevIndex - 1 + slidePictures.length) % slidePictures.length);
    };
  
    const handleNext = () => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % slidePictures.length);
    };

    // ---------------------- FIN DEL SLIDER ------------------- */

    return (
        <div>
            <div>
                <Navbar />
            </div>
            <div>
                <Menu />
            </div>
            <div>
                <Slider />
            </div>
            <link href="https://fonts.googleapis.com/css2?family=Raleway:wght@400;700&display=swap" rel="stylesheet"></link>
            {/* Slider 
            <div>
        <div className="slide-container">
          <div className="slide-pictures">
          {slidePictures.length > 0 ? (
  <div className="slide-pictures">
    {slidePictures.map((picture, index) => (
      <div key={index} className={index === currentIndex ? "slide active" : "slide"}>
        <img src={picture.url} alt={`Slide ${index}`} />
      </div>
    ))}
  </div>
) : (
  <div className="slide-pictures">
    <p>Loading...</p>
  </div>
)}
          </div>
            <div className="slide-controls">
            <button onClick={handlePrev}>&lt;</button>
            <button onClick={handleNext}>&gt;</button>
            </div>
          </div>
        </div>
          Slider */}
        <div>

            <div className="cards-container">
                {allProducts?.map((el) => {
                    return (
                        <div key={el.id}>
                            <Card
                                images={el.images}
                                name={el.name}
                                id={el.id}
                            />
                        </div>
                    );
                })}
            </div>
        </div>
        <div>
            < Footer />
        </div>
        </div>
    )
}
