import React, { useEffect, useRef, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getProducts, getLineProducts } from '../Redux/Actions';
import Card from './Card';
import Navbar from "./Navbar";
import Menu from "./Menu";
import Footer from "./Footer";
import "./Home.css";

export default function Home() {
  const dispatch = useDispatch();
  const allProducts = useSelector((state) => state.products);
  const selectedCategory = useSelector((state) => state.selectedCategory);
  const cardsContainerRef = useRef(null);
  const [visibleCards, setVisibleCards] = useState(4);

  useEffect(() => {
    dispatch(getProducts());
  }, [dispatch]);

  useEffect(() => {
    if (selectedCategory) {
      dispatch(getLineProducts(selectedCategory));
    }
  }, [dispatch, selectedCategory]);

  const scrollLeft = () => {
    if (cardsContainerRef.current) {
      cardsContainerRef.current.scrollBy({
        left: -300,
        behavior: "smooth",
      });
    }
  };

  const scrollRight = () => {
    if (cardsContainerRef.current) {
      cardsContainerRef.current.scrollBy({
        left: 300,
        behavior: "smooth",
      });
    }
  };

  const handleCardResize = () => {
    const containerWidth = cardsContainerRef.current.offsetWidth;
    const cardWidth = 300;
    const newVisibleCards = Math.floor(containerWidth / cardWidth);
    setVisibleCards(newVisibleCards);
  };

  useEffect(() => {
    handleCardResize();
    window.addEventListener("resize", handleCardResize);
    return () => {
      window.removeEventListener("resize", handleCardResize);
    };
  }, []);

  const visibleProducts = allProducts.slice(0, visibleCards);

  return (
    <div>
      <Navbar />
      <Menu />
      <link href="https://fonts.googleapis.com/css2?family=Raleway:wght@400;700&display=swap" rel="stylesheet" />
      <div className="scroll-arrows">
        <button className="arrow-prev" onClick={scrollLeft}>&#60;</button>
      </div>
      <div className="cards-container" ref={cardsContainerRef}>
        {visibleProducts.map((el) => (
          <Card key={el.id} images={el.images} name={el.name} price={el.price} id={el.id} />
        ))}
      </div>
      <div className="scroll-arrows">
        <button className="arrow-next" onClick={scrollRight}>&#62;</button>
      </div>
      {allProducts.length > visibleCards && (
        <div className="scroll-arrows">
          <button className="arrow-prev" onClick={scrollLeft}>&#60;</button>
        </div>
      )}
      <Footer />
    </div>
  );
}
