import { GET_PRODUCTS, GET_PRODUCT_DETAIL, GET_LINE_PRODUCTS, SET_LINE, FOOTER, SEARCH_SUCCESS, SET_SEARCH, CREATE_USER } from "./Actions";


const initialState = {
    products: [],
    filtered: [],
    detail: [],
    selectedCategory: "",
    footer: false,
    isSearch: false,
    // searchResults: [], 
    // searchError: null,
    lineProducts: [],
    isLine: false,
    userInfo: {},
}

function reducer (state = initialState, action) {
switch (action.type) {
    case GET_PRODUCTS:
        return{
            ...state,
            products: action.payload,
            filtered: action.payload,
        };
    case GET_PRODUCT_DETAIL:
        return{
            ...state,
            detail: action.payload,
        }

        case GET_LINE_PRODUCTS:
          const allLine = state.filtered;
          const unifiedTypes = allLine.map(e => {
            if (e.types.length > 0) {
              // Si el pokémon tiene tipos en la propiedad `types`, se toman esos tipos
              return { ...poke, allTypes: poke.types.map(type => type.name) };
            } else {
              // Si el pokémon tiene tipos en la propiedad `pokemonTypes`, se toman esos tipos
              return { ...poke, allTypes: poke.pokemonTypes.split(",") };
            }
          });
        

      case SET_LINE:
        return {
        ...state,
        isLine: true,
      };

        case FOOTER:
      return {
        ...state,
        footer: !state.footer,
      };

      ////
      case SEARCH_SUCCESS:
        return {
            ...state,
            products: action.payload,
        };
      
      case SET_SEARCH:
      return {
        ...state,
        isSearch: true,
      };

      case CREATE_USER:
        if (action.payload.id) {
          return {
            ...state,
            userInfo: { status: "User Created" },
          };
        }
     

    default:
        return state
      }}
      export default reducer;
