import React from "react";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { getDetail } from "../Redux/Actions";
import "./Details.css";
import Navbar from "./Navbar";
import Menu from "./Menu";

export default function ProductDetail(){
    const { id } = useParams()
    const details = useSelector((state) => state.detail);
    const dispatch = useDispatch();
    let imageUrls = [];
    const [selectedImage, setSelectedImage] = useState(null);

    useEffect( () => { 
        dispatch(getDetail(id));
    },[dispatch, id]);

    if (details && details.images) {
      imageUrls = details.images.split(",");
      imageUrls = imageUrls.map((img) => img.trim());
    }

   
    useEffect(() => {
      if (imageUrls.length > 0 && !selectedImage) {
        setSelectedImage(imageUrls[0]);
      }
    }, [imageUrls, selectedImage]);
  
    return (

      <div className="contenedor-principal">
              <div>
      <Navbar />
  </div>
  <div>
      <Menu />
  </div>
        <link
          href="https://fonts.googleapis.com/css2?family=Raleway:wght@400;700&display=swap"
          rel="stylesheet"
        ></link>
        {details && (
          <div className="contenedor">
            <div className="card-imagen">
              {imageUrls.map((img) => (
                <img
                  src={img.trim()}
                  alt="Not found"
                  key={img}
                  width="100"
                  height="100"
                  onClick={() => setSelectedImage(img.trim())}
                />
              ))}
            </div>
            <div className="detalle">
              <p className="detalle-name">{details.name}</p>
              <p className="detalle-texto">Price: </p>{details.price}
              <p className="detalle-texto">Stock:</p> {details.stock}
              <p className="detalle-texto">Detail:</p> {details.details}
              <p className="detalle-texto">Line:</p> {details.line}
            </div>
            {selectedImage && (
              <img
                src={selectedImage}
                alt="Not found"
                className="enlarged-image"
              />
            )}
          </div>
        )}
      </div>
    );
}
