import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { getNameProduct } from "../Redux/Actions";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch } from '@fortawesome/free-solid-svg-icons';
import './SearchBar.css'; // Importa el archivo CSS para los estilos personalizados

export default function SearchBar() {
  const dispatch = useDispatch();
  const [name, setName] = useState("");

  function handleInputChange(e) {
    setName(e.target.value);
  }

  function handleSubmit(e) {
    e.preventDefault();
    dispatch(getNameProduct(name));
  }

  function handleKeyPress(e) {
    if (e.key === 'Enter') {
      handleSubmit(e);
    }
  }

  return (
    <div className="search-bar">
      <div className="search-container">
        <input
          id="search-input"
          type="text"
          placeholder="Search product"
          onChange={handleInputChange}
          onKeyPress={handleKeyPress}
          className="search-input"
        />
        <button type="submit" onClick={handleSubmit} className="search-button">
          <FontAwesomeIcon icon={faSearch} className="search-icon" />
        </button>
      </div>
    </div>
  );
}
