import React, { useEffect } from "react";
import { useAuth0 } from "@auth0/auth0-react";
import { createUser, getUserInfo } from "../../Redux/Actions";
import { useDispatch, useSelector } from "react-redux";
import Navbar from "../Navbar";
import Menu from "../Menu";
import Footer from "../Footer";




const UserProfile = ({id}) => {
  const dispatch = useDispatch();
  const { user, isAuthenticated, isLoading } = useAuth0();
  const userInfo = useSelector((state) => state.userInfo);

  // if(userInfo, user) {
  //   for (const userObj of userInfo) {
  //     if (userObj.email === user.email) {
  //       console.log (userObj); // Devuelve el objeto userInfo del usuario encontrado
  //     }
  //   }
  // }
  

  useEffect(() => {
    dispatch(getUserInfo(id));
  }, [dispatch, id]);

  useEffect(() => {
    if (isAuthenticated && !isLoading) {
      const userDb = {
        email: user.email,
        fullname: user.name,
        profile: user.nickname,
        avatar: user.picture,
      };

      dispatch(createUser(userDb));
     
    }
  }, [dispatch, isAuthenticated, isLoading, user]);

  if (isLoading) {
    // You can add a loading spinner or placeholder while the authentication is in progress
    return <div>Loading...</div>;
  }

  useEffect(() => {
    // Si userInfo y user están disponibles, busca el usuario que coincide con el email
    if (userInfo && user) {
      for (const userObj of userInfo) {
        if (userObj.email === user.email) {
          setUserObj(userObj); // Almacena el usuario encontrado en el estado local
          break; // Puedes detener la búsqueda si ya encontraste al usuario
        }
      }
    }
  }, [userInfo, user]);

  return (
    isAuthenticated && (
      <div>
        <div>
          <Navbar />
        </div>
        <div>
          <Menu />
        </div>
        <img src={user.picture} alt={user.name} />
        <h2>{user.name}</h2>
        <p>{user.email}</p>
        <h2>{user.nickname}</h2>
        <div>

          <Footer />
        </div>
      </div>
    )
  );
};

export default UserProfile;
