  // console.log(info.email)

  return (
    isAuthenticated && (
      <div>
        <div>
          <Navbar />
        </div>
        <div>
          <Menu />
        </div>
        <img src={user.picture} alt={user.name} />
        <h2>{user.name}</h2>
        <p>{userInfo.email}</p>
        <h2>{user.nickname}</h2>
        <div>

          <Footer />
        </div>
      </div>
    )
  );
};

export default UserProfile;
