// Componente donde quieres mostrar los productos de línea según la categoría seleccionada
// Menu.js
import React from "react";
import "./Menu.css";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { getLineProducts, setSelectedCategory } from "../Redux/Actions";

export default function Menu() {
  const dispatch = useDispatch();
  const categories = ['Bathroom', 'Kitchen', 'Home & Deco', 'Personal care', 'Disposables'];

  const selectedCategory = useSelector((state) => state.selectedCategory);

  function handleClick(category) {
    const encodedCategory = encodeURIComponent(category.toLowerCase().replace(/\s/g, '-'));
    dispatch(getLineProducts(encodedCategory));
    dispatch(setSelectedCategory(category)); // Actualizamos la categoría seleccionada en el estado de Redux
  }

  return (
    <div className="menu">
      <link href="https://fonts.googleapis.com/css2?family=Raleway:wght@400;700&display=swap" rel="stylesheet"></link>
      <ul className="menu ul">
        {categories.map((category, index) => (
          <li key={index} className="menu li">
            <Link
              to={`/category/${category.toLowerCase().replace(/\s/g, '-')}`}
              className={`menu-link ${selectedCategory === category ? 'active' : ''}`}
              onClick={() => handleClick(category)}
            >
              {category}
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
}
