import React from "react";
import { Link } from "react-router-dom";
import "./Card.css"; // Importar archivo CSS para estilos personalizados

export default function Card({ id, images, name, price }) {
  const [startIndex, setStartIndex] = useState(0);
  const cardsPerPage = 4;

  const imageUrls = images.split(", ");
  const firstImageUrl = imageUrls[0]; 

  const handlePrevClick = () => {
    setStartIndex((prevIndex) => Math.max(prevIndex - cardsPerPage, 0));
  };

  const handleNextClick = () => {
    setStartIndex((prevIndex) =>
      Math.min(prevIndex + cardsPerPage, cards.length - cardsPerPage)
    );
  };

  const visibleCards = cards.slice(startIndex, startIndex + cardsPerPage);


  return (
    <div>
    {visibleCards.map((card, index) => (
      <div key={index} className="card">
        <img src={firstImageUrl} alt="no" className="imagenes"/>
        <h2>{card.name}</h2>
        <span>${card.price}</span>
      </div>
    ))}
    <div>
      <button onClick={handlePrevClick} disabled={startIndex === 0}>
        &lt; Anterior
      </button>
      <button
        onClick={handleNextClick}
        disabled={startIndex + cardsPerPage >= cards.length}
      >
        Siguiente &gt;
      </button>
    </div>
  </div>
);
};
