// Componente donde quieres mostrar los productos de línea según la categoría seleccionada
import React, { useState } from "react";
import "./Menu.css";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { getLineProducts } from "../Redux/Actions";

export default function Menu() {
  const dispatch = useDispatch();
  const categories = ['Bathroom', 'Kitchen', 'Home & Deco', 'Personal care', 'Disposables'];

  // Estado local para almacenar la categoría seleccionada
  const [selectedCategory, setSelectedCategory] = useState(null);

  const lines = useSelector((state) => state.lineProducts);
  

  const filteredProducts = lines.filter((e) => e.lines === selectedCategory);
  console.log(filteredProducts)
  // Función para manejar el clic en una categoría
  function handleClick(category) {
    const encodedCategory = encodeURIComponent(category.toLowerCase().replace(/\s/g, '-'));
    dispatch(getLineProducts(encodedCategory));
    // Actualizamos la categoría seleccionada en el estado local
    setSelectedCategory(category);
  }

  return (
    <div>
      <h2>Productos de la categoría: {selectedCategory}</h2>
      <ul>
        {filteredProducts.map((product) => (
          <li key={product.id}>
            <strong>Nombre:</strong> {product.name} <br />
            <strong>Línea:</strong> {product.lines}
          </li>
        ))}
      </ul>
    </div>
  );
};
