import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getProducts, getLineProducts } from '../Redux/Actions';
import Paginado from "./Cards/Paginado";
import Navbar from "./Navbar";
import Menu from "./Menu";
import Footer from "./Footer";
import Slider from "./Slider";
import "./Slider.css";
import "./Home.css";
import { useParams } from "react-router-dom";
import ProductDetail from "./Details";
import { Route, Switch } from "react-router-dom";
import Recommended from "./Cards/Recommended";

export default function Home() {
  const dispatch = useDispatch();
  const { categoryName } = useParams();
  const allProducts = useSelector((state) => state.products);
  const selectedCategory = useSelector((state) => state.selectedCategory);
  const isSearch = useSelector((state) => state.isSearch);
  const isLine = useSelector((state) => state.isLine);

  const [searchResults, setSearchResults] = useState([]); // Nuevo estado para almacenar los productos buscados

  const filteredProducts = allProducts.filter(
    (product) => product.category === categoryName
  );

  useEffect(() => {
    dispatch(getProducts());
  }, [dispatch]);

  useEffect(() => {
    if (selectedCategory) {
      dispatch(getLineProducts(selectedCategory));
    }
  }, [dispatch, selectedCategory]);

  // Función para manejar los resultados de búsqueda
  const handleSearchResults = (results) => {
    setSearchResults(results);
  };

  return (
    <div>
      <div>
        <Navbar />
      </div>
      <div>
        <Menu />
      </div>
      <div>
        {!(isSearch || isLine) && <Slider />}
      </div>
      <link
        href="https://fonts.googleapis.com/css2?family=Raleway:wght@400;700&display=swap"
        rel="stylesheet"
      ></link>

      <div>
        {/* Renderizar los productos buscados si hay resultados */}
        {isSearch && searchResults.length > 0 && (
          <Recommended cards={searchResults} showAll={true} />
        )}

        {/* Renderizar los productos recomendados */}
        {!(isSearch || isLine) && (
          <Recommended cards={filteredProducts} showAll={true} />
        )}

        {/* Renderizar los productos paginados */}
        <div>
          <Paginado cards={filteredProducts} showAll={true} />
        </div>
      </div>
      <div>
        <Footer />
      </div>
    </div>
  );
}
